import React from "react";

const Note = (props) => {

    const deleteNote = () => {
        props.deleteItem(props.id);
    };


    return (
        <>
            <div className="note_main">
                <div contentEditable="true" className="note" role="textbox">
                    <h2 >{props.title}</h2>
                    <p >{props.content}</p>
                    <button className="delete_btn" onClick={deleteNote}>
                        Delete
                    </button>
                </div>
            </div>
        </>
    );
};

export default Note;
