import React, { useState } from "react";

const Note = (props) => {
  const [editingTitle, setEditingTitle] = useState(false);
  const [editingContent, setEditingContent] = useState(false);
  const [selectedColor, setSelectedColor] = useState(null);

  const handleColorClick = (color) => {
    setSelectedColor(color);
  };

  const handleEditTitle = () => {
    setEditingTitle(true);
  };

  const handleEditContent = () => {
    setEditingContent(true);
  };

  const handleSaveTitle = (event) => {
    props.editItem(props.id, { title: event.target.innerText });
    setEditingTitle(false);
  };

  const handleSaveContent = (event) => {
    props.editItem(props.id, { content: event.target.innerText });
    setEditingContent(false);
  };

  const deleteNote = () => {
    props.deleteItem(props.id);
  };

  return (
    <>
      <div className="note_main">
        <div style={{ backgroundColor: selectedColor }} className="note" role="textbox">
          {editingTitle ? (
            <h2 className="tit" contentEditable="true" onBlur={handleSaveTitle}>
              {props.title}
            </h2>
          ) : (
            <h2 className="tit" onClick={handleEditTitle}>
              {props.title}
            </h2>
          )}

          {editingContent ? (
            <p className="cont" contentEditable="true" onBlur={handleSaveContent}>
              {props.content}
            </p>
          ) : (
            <p className="cont" onClick={handleEditContent}>
              {props.content}
            </p>
          )}

          <button className="delete_btn" onClick={deleteNote}>
            Delete
          </button>
        </div>

        <div className="color">
          {/* Your color buttons here */}
        </div>
      </div>
    </>
  );
};

export default Note;
