import React, { useState } from "react";

const Note = (props) => {

    const deleteNote = () => {
        props.deleteItem(props.id);
    };


    const [selectedColor, setSelectedColor] = useState(null);

    const handleColorClick = (color) => {
        setSelectedColor(color);
    };



    return (
        <>
            <div className="note_main">

                <div style={{ backgroundColor: selectedColor }} contentEditable="true" className="note" role="textbox">
                    <h2 >{props.title}</h2>
                    <p >{props.content}</p>
                    <button className="delete_btn" onClick={deleteNote}>
                        Delete
                    </button>
                </div>

                <div>
                    <button onClick={() => handleColorClick('red')}>Red</button>
                    <button onClick={() => handleColorClick('green')}>Green</button>
                    <button onClick={() => handleColorClick('blue')}>Blue</button>
                </div>
            </div>

        </>
    );
};

export default Note;
