import React, { useState } from "react";
import EditNote from "./EditNote";
const Note = (props) => {


    const [notes, setNotes] = useState([
        { id: 1, content: 'Note 1' },
        { id: 2, content: 'Note 2' },
        // ... other notes
    ]);

    const [editingNoteId, setEditingNoteId] = useState(null);

    const handleUpdateNote = (updatedNote) => {
        setNotes((prevNotes) =>
            prevNotes.map((note) =>
                note.id === editingNoteId ? { ...note, ...updatedNote } : note
            )
        );
        setEditingNoteId(null);
    };

    const handleEditNote = (noteId) => {
        setEditingNoteId(noteId);
    };

    const handleCancelEdit = () => {
        setEditingNoteId(null);
    };

    const deleteNote = () => {
        props.deleteItem(props.id);
    };


    const [selectedColor, setSelectedColor] = useState(null);

    const handleColorClick = (color) => {
        setSelectedColor(color);
    };



    return (
        <>
            <div className="note_main">

                <div>
                    {notes.map((note) => (
                        <div key={note.id}>
                            {editingNoteId === note.id ? (
                                <EditNote
                                    note={note}
                                    onUpdateNote={handleUpdateNote}
                                    onCancelEdit={handleCancelEdit}
                                />
                            ) : (
                                <div>
                                    <p>{note.content}</p>
                                    <button onClick={() => handleEditNote(note.id)}>Edit</button>
                                </div>
                            )}
                        </div>
                    ))}
                </div>

                <div style={{ backgroundColor: selectedColor }} className="note" role="textbox">
                    <h2 className="tit" contentEditable="true" >{props.title}</h2>
                    <p className="cont" contentEditable="true" >{props.content}</p>
                    <button className="delete_btn" onClick={deleteNote}>
                        Delete
                    </button>
                </div>

                <div className="color">
                    <button id="r" onClick={() => handleColorClick('rgba(253, 153, 255, 1')}></button >
                    <button id="g" onClick={() => handleColorClick('rgba(145, 244, 143, 1')}></button >
                    <button id="b" onClick={() => handleColorClick('rgba(255, 245, 153, 1')}></button >
                    <button id="p" onClick={() => handleColorClick('rgba(158, 255, 255, 1')}></button >
                    <button id="c" onClick={() => handleColorClick('rgba(182, 156, 255, 1')}></button>
                </div>

            </div>

        </>
    );
};

export default Note;
