import React, { useState } from "react";

const Counter = () => {
    const [num, setNum] = useState(0)

    const incNum = () => {
        setNum(num + 1)
    };

    const decNum = () => {
        if (num > 0) {
            setNum(num - 1)
        } else {
            alert("Aur kam nhi hoga")
            setNum(0)
        }
    };

    return (
        <>
            <div className="miv">
                <h1> {num} </h1>
                <div className="btn">
                    <button onClick={incNum}>+</button>
                    <button onClick={decNum}>-</button>
                </div>
            </div>

        </>
    );
};

export default Counter;
