import React from "react";

let date = new Date();
date = date.getHours();
let greeting = "";
const style ={ };

if (date >0 && date <12) {
  greeting = "Good Morning";
  style.color="green";
} else if (date >=12 && date <=19){
  greeting = "Good Evening";
} else greeting = "Good Night";




const App =() => {
  return(
    <div>
      
      <h1>Welcome sir, {greeting}, it's {date} </h1>
    </div>

  )
};

export default App;
