import React from "react";


let time = new Date();
time = time.getHours();
let greeting = "";
const css = {};

if (time =>1 && time <12){
    greeting = "Good Morning";
    css.color="green";
} else if (time >=12 && time <=19){
    greeting ="Good Evening";
    css.color="orange";
} else{
    greeting = "Good Night";
    css.color="blue";
}









const App = ()=> {
    return(
        <h1>Hi, Sir <span style= {css} >{greeting} </span></h1>
        )
}

export default App;
