    setName(event.target.value);
  };

  const submit =(event) => {
    console.log(event.target.value);
  };
  
  return(
  <>
  <h1>Hello {name}</h1>
  <input type="text" placeholder="Enter Your Name" onChange={inputName}/>
  <button>Submit</button>
  </>
  );
};








