import React, { useState } from "react";
import Header from "./Header";
import Footer from "./Footer";
import CreatNotes from "./CreatNotes";
import Note from "./Note";



const App = () => {

  const [addItem, setAddItem] = useState([]);


  const addNote = (note) => {
    setAddItem((prevData) => {
      return [...prevData, note];
    });
    console.log(note);
  };

  const onDelete = (id) => {
    setAddItem((olddata) =>
      olddata.filter((currdata, index) => {
        return index !== id;
      })
    );
  };

  const [notes, setNotes] = useState([addNote]);

  const editItem = (id, editedTitle, editedContent, selectedColor) => {
    // Implement logic to edit the item and update the state
    const updatedNotes = notes.map((note) =>
      note.id === id
        ? { ...note, title: editedTitle, content: editedContent, color: selectedColor }
        : note
    );
    setNotes(updatedNotes);
  };


  return (
    <>
      <Header />
      <CreatNotes passNote={addNote} />
      

      {addItem.map((val, index) => {
        return (

          <Note
            key={index}
            id={index}
            title={val.title}
            content={val.content}
            deleteItem={onDelete}
            editItem={editItem} 
          />
        );
      })}

      <Footer />

    </>
  );
};

export default App;
