import React, { useState } from 'react';
import BlogList from './BlogList';
import BlogForm from './BlogForm';
import BlogPost from './BlogPost';

const App = () => {
    const [blogs, setBlogs] = useState([]);
    const [selectedBlog, setSelectedBlog] = useState(null);

    const handleCreateBlog = (blog) => {
        setBlogs([...blogs, { id: Date.now(), ...blog }]);
    };

    const handleEditBlog = (id) => {
        const blogToEdit = blogs.find((blog) => blog.id === id);
        setSelectedBlog(blogToEdit);
    };

    const handleUpdateBlog = (updatedBlog) => {
        const updatedBlogs = blogs.map((blog) => (blog.id === updatedBlog.id ? updatedBlog : blog));
        setBlogs(updatedBlogs);
        setSelectedBlog(null);
    };

    const handleDeleteBlog = (id) => {
        const updatedBlogs = blogs.filter((blog) => blog.id !== id);
        setBlogs(updatedBlogs);
    };

    return (
        <div>
            <BlogList blogs={blogs} onEdit={handleEditBlog} onDelete={handleDeleteBlog} />
            {selectedBlog ? (
                <BlogForm onSubmit={handleUpdateBlog} initialBlog={selectedBlog} />
            ) : (
                <BlogForm onSubmit={handleCreateBlog} />
            )}
            {selectedBlog && <BlogPost blog={selectedBlog} />}
        </div>
    );
};

export default App;
