import React from "react";

const date = new Date()
date = date.getHours();
const greeting = "";

if (date >0 && date <12){
  greeting = "Good Morning"
} else if (date >=12 && date <=19){
  greeting ="Good Evening"
} else greeting = "Good Night"

const App =() => {
  return(
    <div>
      <h1>Welcome sir, {greeting} </h1>
    </div>

  )
};

export default App;
