import React, { useState } from "react";

const App = () => {

let time = new Date().toLocaleTimeString();

const [cTime, setTime] = useState(time);

const updateTime = () => {
  time = new Date().toLocaleTimeString();
  setTime(time);
};

return (
<>
    <h1>{cTime}</h1>
    
        <button>Get{updateTime}</button>
</>
);

};











export default App;
