import React, { useState } from "react";
import Header from "./Header";
import Footer from "./Footer";
import CreatNotes from "./CreatNotes";
import Note from "./Note";



const App = () => {

  const [addItem, setAddItem] = useState([]);


  const addNote = (note, index, selec) => {
    setAddItem((prevData) => {
      return [...prevData, note];
    });
    console.log(index);
    console.log(note);
    console.log(selectedColor);

  };

  const onDelete = (id) => {
    setAddItem((olddata) =>
      olddata.filter((currdata, index) => {
        return index !== id;
      })
    );
  };

  const onEdit = (id, editedTitle, editedContent, selectedColor) => {
    console.log(id);
    console.log("New Title:", editedTitle);
    console.log("New Content:", editedContent);
    console.log("New Color:", selectedColor);

    const updatedNotes = addItem.map((note, index) =>
      index === id
        ? { ...note, title: editedTitle, content: editedContent, color: selectedColor }
        : note
    );
    setAddItem(updatedNotes);

    console.log("Updated Notes:", updatedNotes);
  };


  return (
    <>
      <Header />
      <CreatNotes passNote={addNote} />


      {addItem.map((val, index) => {
        return (

          <Note
            key={index}
            id={index}
            title={val.title}
            content={val.content}
            deleteItem={onDelete}
            editItem={onEdit}
          />
        );
      })}

      <Footer />

    </>
  );
};

export default App;
