import React from "react";

const Function =()=> {
    let date = new Date();
date = date.getHours();
let greeting = "";
const cssStyle = {};

if (date >=1 && date <12) {
  greeting = "Good Morning";
  cssStyle.color="green" ;
} else if (date >=12 && date <19){
  greeting = "Good Evening" ;
  cssStyle.color= "pink";
} else {
  greeting = "Good Night";
  cssStyle.color= "black" ;
}

}

export default Function;
