import React, { useState } from "react";
import Header from "./Header";
import Footer from "./Footer";
import CreatNotes from "./CreatNotes";
import Note from "./Note";



const App = () => {

  const [addItem, setAddItem] = useState([]);

  const [notes,setNotes] = useState([]);
  

  const addNote = (note) => {
    setAddItem((prevData) => {
      return [...prevData, note];
    });
    console.log(note);
  };

  const onDelete = (id) => {
    setAddItem((olddata) =>
      olddata.filter((currdata, index) => {
        return index !== id;
      })
    );
  };


  return (
    <>
      <Header />
      <CreatNotes passNote={addNote} />
      

      {addItem.map((val, index) => {
        return (

          <Note
            key={index}
            id={index}
            title={val.title}
            content={val.content}
            deleteItem={onDelete}
          />
        );
      })}

      <Footer />

    </>
  );
};

export default App;
