import React from "react";

let date = new Date(2023,5,5,5);
date = date.getHours();
let greeting = "";
const cssStyle = {color:green};

if (date >=1 && date <12) {
  greeting = "Good Morning"
  cssStyle.color="green" ;
} else if (date >=12 && date <19){
  greeting = "Good Evening" 
  cssStyle.color= "pink";
} else greeting = "Good Night"
cssStyle.color= "black" ;




const App =() => {
  return(
      <h1 style={cssStyle}>Welcome sir, {greeting} </h1>
    

  )
};

export default App;
