import React, { useState } from "react";

const Note = (props) => {
    const [isEditing, setIsEditing] = useState(false);
    const [editedTitle, setEditedTitle] = useState(props.title);
    const [editedContent, setEditedContent] = useState(props.content);
    const [selectedColor, setSelectedColor] = useState(props.color || null);

    const deleteNote = () => {
        props.deleteItem(props.id);
    };

    const handleColorClick = (color) => {
        setSelectedColor(color);
    };

    const handleEditClick = () => {
        setIsEditing(true);
    };

    const updateItem = (id, editedTitle, editedContent, selectedColor) => {
        setNotes((prevNotes) =>
          prevNotes.map((note) =>
            note.id === id ? { ...note, title: editedTitle, content: editedContent, color: selectedColor } : note
          )
        );
      

    const handleSaveClick = () => {
        setIsEditing(false);
        props.updateItem(props.id, editedTitle, editedContent, selectedColor);
    };
};

    const handleCancelClick = () => {
        setIsEditing(false);
        setEditedTitle(props.title);
        setEditedContent(props.content);
    };

    return (
        <>
            <div className="note_main">
                <div style={{ backgroundColor: selectedColor }} className="note">
                    {isEditing ? (
                        <>
                            <input
                                type="text"
                                value={editedTitle}
                                onChange={(e) => setEditedTitle(e.target.value)}
                            />
                            <textarea
                                value={editedContent}
                                onChange={(e) => setEditedContent(e.target.value)}
                            />
                            <button onClick={handleSaveClick}>Save</button>
                            <button onClick={handleCancelClick}>Cancel</button>
                        </>
                    ) : (
                        <>
                            <h2 className="tit">{props.title}</h2>
                            <p className="cont">{props.content}</p>
                            <button className="delete_btn" onClick={deleteNote}>
                                Delete
                            </button>
                            <button onClick={handleEditClick}>Edit</button>
                        </>
                    )}
                </div>

                <div className="color">
                    <button id="r" onClick={() => handleColorClick("rgba(253, 153, 255, 1")}></button>
                    <button id="g" onClick={() => handleColorClick("rgba(145, 244, 143, 1")}></button>
                    <button id="b" onClick={() => handleColorClick("rgba(255, 245, 153, 1")}></button>
                    <button id="p" onClick={() => handleColorClick("rgba(158, 255, 255, 1")}></button>
                    <button id="c" onClick={() => handleColorClick("rgba(182, 156, 255, 1")}></button>
                </div>
            </div>
        </>
    );
};

export default Note;
