import React from "react";

let date = new Date();

let greeting = "";
const cssStyle = {};

if (date >=1 && date <12) {
  greeting = "Good Morning";
  cssStyle.color="green" ;
} else if (date >=12 && date <19){
  greeting = "Good Evening" ;
  cssStyle.color= "pink";
} else {
  greeting = "Good Night";
  cssStyle.color= "black" ;
}



const App =() => {
  return(
    <div>
      <h1>Welcome sir, <span style={cssStyle}> {greeting} </span> 
      </h1>
    </div>

  )
};

export default App;
