import React, { useState } from "react";




const App = () =>{
    const state = useState();
    
    const [count, setCount] = useState(0);
    
    const IncNum =() => {
        setCount(count + 1);
    };
    const IncDec =() =>{
        setCount(count -1);
    };
    
    return <> 
        <h1>{count}</h1>
        <button onClick={IncNum}>Click To Increase </button>
        <button onClick={IncDec}>Cilck To Decrease</button>
</> 
};

export default App;
