import React, { useState } from "react";




const App = () =>{
    const state = useState();
    
    const [count, setCount] = useState(0);
    
    const IncNum =() => {
        setCount(count + 10);
    };
    const IncDec =() =>{
        setCount(count -1);
    };
     
    const ReSet =() =>{
        setCount(0);
    };

    return <> 
        <h1>{count}</h1>
        <div>
        <button onClick={IncNum}>Click To Increase</button>
        <button onClick={IncDec}>Cilck To Decrease</button>
        <button onClick={ReSet}>Reset</button>
        </div>
</> 
};

export default App;
