import React, { useState } from "react";

const Note = (props) => {
    const [editing, setEditing] = useState(false);
    const [editedTitle, setEditedTitle] = useState(props.title);
    const [editedContent, setEditedContent] = useState(props.content);
    const [selectedColor, setSelectedColor] = useState(null);

    const handleColorClick = (color) => {
        setSelectedColor(color);
    };

    const handleEditToggle = () => {
        setEditing(!editing);
    };

    const handleSave = () => {
        // Save the edited title and content
        props.editItem(props.id, { title: editedTitle, content: editedContent });
        setEditing(false);
    };

    const handleTitleChange = (event) => {
        setEditedTitle(event.target.innerText);
    };

    const handleContentChange = (event) => {
        setEditedContent(event.target.innerText);
    };

    const deleteNote = () => {
        props.deleteItem(props.id);
    };

    return (
        <>
            <div className="note_main">
                <div
                    style={{ backgroundColor: selectedColor }}
                    className="note"
                    role="textbox"
                >
                    <h2
                        className={`tit ${editing ? "editable" : ""}`}
                        contentEditable={editing}
                        onClick={handleEditToggle}
                        onBlur={handleTitleChange}
                    >
                        {editedTitle}
                    </h2>

                    <p
                        className={`cont ${editing ? "editable" : ""}`}
                        contentEditable={editing}
                        onClick={handleEditToggle}
                        onBlur={handleContentChange}
                    >
                        {editedContent}
                    </p>

                    {editing ? (
                        <button className="save_btn" onClick={handleSave}>
                            Save
                        </button>
                    ) : (
                        <button className="edit_btn" onClick={handleEditToggle}>
                            Edit
                        </button>
                    )}

                    <button className="delete_btn" onClick={deleteNote}>
                        Delete
                    </button>
                </div>

                <div className="color">
                    <button id="r" onClick={() => handleColorClick('rgba(253, 153, 255, 1)')}></button>
                    <button id="g" onClick={() => handleColorClick('rgba(145, 244, 143, 1)')}></button>
                    <button id="b" onClick={() => handleColorClick('rgba(255, 245, 153, 1)')}></button>
                    <button id="p" onClick={() => handleColorClick('rgba(158, 255, 255, 1)')}></button>
                    <button id="c" onClick={() => handleColorClick('rgba(182, 156, 255, 1)')}></button>

                </div>
            </div>
        </>
    );
};

export default Note;
