import React, { useState } from "react";



const CreatNotes = (props) => {

        const [note,setNote] = useState({
            title: "",
            content: "",
        });

        const InputEvent =(event) =>{

            

            const {name,value} = event.target;

            setNote((prevData) =>{
                return{
                    ...prevData,
                    [name]:value
                }
            })
        };

        const addEvent =() =>{
            props.passNote(note);
        };

    return <>
        <div className="main_note">
            <form className="form">
                <input className="title" type="text" name="title" value={note.title} onChange= {InputEvent} placeholder="Title" autoComplete="off" />
                <textarea className="disc" rows="" colums="" name="content" value={note.content} onChange= {InputEvent} placeholder="Write a note" />
                <button className="btncontent" onClick={addEvent}>
                          
                </button>
            </form>
        </div>

    </>

};

export default CreatNotes;
