import React, { useState } from "react";

const Counter = () => {
    const [num, setNum]=useState(0)

    const incNum =()=>{ 
        setNum(num+1)
    };
    
    const decNum =()=>{
        setNum(num-1)
    }

    return (
        <>
            <div className="main_div">
                <h1> {num} </h1>
                <div className="btn">
                    <button onClick={incNum}>+</button>
                    <button >-</button>
                </div>
            </div>

        </>
    );
};

export default Counter;
