import React, { useState } from "react";

const Note = (props) => {
    const [editMode, setEditMode] = useState(false);
    const [editedTitle, setEditedTitle] = useState(props.title);
    const [editedContent, setEditedContent] = useState(props.content);
    const [selectedColor, setSelectedColor] = useState(null);

    const deleteNote = () => {
        props.deleteItem(props.id);
    };

    const handleColorClick = (color) => {
        setSelectedColor(color);
    };

    const toggleEditMode = () => {
        setEditMode(!editMode);
    };

    const saveChanges = () => {
        // Save the changes and exit edit mode
        props.editItem(props.id, editedTitle, editedContent, selectedColor);
        setEditMode(false);
    };

    return (
        <>
            <div className="note_main">
                <div style={{ backgroundColor: selectedColor }} className="note" role="textbox">
                    {editMode ? (
                        <>
                            <input
                                type="text"
                                value={editedTitle}
                                onChange={(e) => setEditedTitle(e.target.value)}
                            />
                            <textarea
                                value={editedContent}
                                onChange={(e) => setEditedContent(e.target.value)}
                            />
                        </>
                    ) : (
                        <>
                            <h2 className="tit">{props.title}</h2>
                            <p className="cont">{props.content}</p>
                        </>
                    )}
                    <button className="delete_btn" onClick={deleteNote}>
                        Delete
                    </button>
                    {editMode ? (
                        <button className="save_btn" onClick={saveChanges}>
                            Save
                        </button>
                    ) : (
                        <button className="edit_btn" onClick={toggleEditMode}>
                            Edit
                        </button>
                    )}
                </div>
                <div className="color">
                    <button id="r" onClick={() => handleColorClick('rgba(253, 153, 255, 1')}></button>
                    <button id="g" onClick={() => handleColorClick('rgba(145, 244, 143, 1')}></button>
                    <button id="b" onClick={() => handleColorClick('rgba(255, 245, 153, 1')}></button>
                    <button id="p" onClick={() => handleColorClick('rgba(158, 255, 255, 1')}></button>
                    <button id="c" onClick={() => handleColorClick('rgba(182, 156, 255, 1')}></button>
                </div>
            </div>
        </>
    );
};

export default Note;
