import React, { useState } from "react";

const App = () => {

let time = new Date().toLocaleTimeString();

const [cTime, setTime] = useState(time);

const updateTime = () => {
  time = new Date().toLocaleTimeString();
  setTime(time);
}

return (
<>
<div>
    <h1>{cTime}</h1>
      <div>
        <button>{updateTime}</button>
    </div>
  </div>
</>
);

};











export default App;
